<template>
  <div class="container-xl main-content">
    <!-- User Account Section -->
    <div class="account-section">
      <div class="col-md-auto">
        <UserHeader :userName="userName" :rating="rating" :id="id" />
      </div>
    <div class="follow-section">
      <!-- Display Followers Count -->
      <div class="followers-section" @click="toggleFollowersPopup">
        <div class="follow-count">
          <h6 class="count">{{ followersData.length }}</h6>
          <h6 class="section-title">Followers</h6>
        </div>
      </div>
      <!-- Display Following Count -->
      <div class="following-section" @click="toggleFollowingPopup">
        <div class="follow-count">
          <h6 class="count">{{ followingData.length }}</h6>
          <h6 class="section-title">Following</h6>
        </div>
      </div>
    </div>
    </div>
    <!-- Recently Visited Restaurants -->
    <div class="recently-visited-section">
      <RecentlyVisitedRestaurants :id="id" />
    </div>
    <!-- Favorites Section -->
    <div class="favorites-section">
      <FavoritesContainer :owner="Owner" />
    </div>
    <!-- Followers and Following Popups -->
    <Popup :visible="showFollowersPopup" title="Followers" :items="followersData" @close="toggleFollowersPopup" />
    <Popup :visible="showFollowingPopup" title="Following" :items="followingData" @close="toggleFollowingPopup" />
  </div>
</template>


<script>
import UserHeader from "@/components/profileView/UserHeader.vue";
import RecentlyVisitedRestaurants from "@/components/profileView/RecentlyVisitedRestaurant.vue";
import FavoritesContainer from "@/components/profileView/FavoritesContainer.vue";
import { Owner } from "@/components/profileView/script/profile.utility";
import Cookies from "js-cookie";
import { getUserById } from "@/api/user";
import Popup from "@/components/profileView/PopUpList.vue";

export default {
  computed: {
    Owner() {
      return { email: this.email, id: this.id, name: this.userName };
    },
  },
  components: {
    RecentlyVisitedRestaurants,
    UserHeader,
    FavoritesContainer,
    Popup
  },
  data() {
    return {
      userName: Cookies.get("userName"),
      email: Cookies.get("userEmail"),
      id: Cookies.get("userId"),
      rating: 0,
      isSwitched: false,
      followersData: [
        { id: 1, username: 'follower1', avatarUrl: 'data:image/jpeg;base64,' },
        { id: 2, username: 'follower2', avatarUrl: 'data:image/png;base64,' },
        { id: 3, username: 'follower3', avatarUrl: 'data:image/jpeg;base64,' }
      ],
      followingData: [
        { id: 4, username: 'following1', avatarUrl: 'data:image/jpeg;base64,' },
        { id: 5, username: 'following2', avatarUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbY-JZGyMinyIgLjEsGfovZW4-_iB2rJCAtg&s' },
        { id: 6, username: 'following3', avatarUrl: 'data:image/jpeg;base64,' }
      ],
      showFollowersPopup: false,
      showFollowingPopup: false
    };
  },
  async created() {
    const userData = await getUserById(Cookies.get("userId"));
    this.userName = userData.name;
    this.email = userData.email;
    this.id = userData.id;
    this.rating = userData.rating;
  },
  methods: {
    toggleFollowersPopup() {
      this.showFollowersPopup = !this.showFollowersPopup;
    },
    toggleFollowingPopup() {
      this.showFollowingPopup = !this.showFollowingPopup;
    }
  }
};
</script>

